SELECT DP.EnglishProductName AS "Product Name",DP.Weight,DP.Color,FRS.SalesAmount,FRS.OrderQuantity,DE.BirthDate AS "Employee Birthday",CONCAT(DD.DayNumberOfMonth,DD.EnglishMonthName,' ',DD.CalendarYear) AS "Order Date",CONCAT(DE.FirstName,' ',DE.MiddleName,' ',DE.LastName) AS "Employee Name",DE.EmailAddress,DE.Phone,DR.BankName AS "Reseller Bank Name",DR.AnnualRevenue "Reseller Annual Revenue",DE.EmployeeKey,DC.CurrencyNameFROM FactResellerSales FRS JOIN DimProduct DP ON FRS.ProductKey=DP.ProductKeyJOIN DimDate DD ON FRS.OrderDateKey=DD.DateKeyJOIN DimEmployee DE ON FRS.EmployeeKey=DE.EmployeeKeyJOIN DimReseller DR ON FRS.ResellerKey=DR.ResellerKeyJOIN DimCurrency DC ON DC.CurrencyKey=FRS.CurrencyKey